                PKG1 [0x03] = B1B2 (BPR1, BPR2)
                PKG1 [0x04] = B1B2 (BAR1, BAR2)
                PKG1 [0x05] = B1B2 (BCC1, BCC2)
                PKG1 [0x06] = B1B2 (BCV1, BCV2)
